import React, { useState, useEffect, createContext, useReducer } from "react";
// import Second27feb from "./27th_feb";
import './style.css';

export const ThemeContext = createContext();

const First20feb = () => {

    const [count, setCount] = useState(0);
    const [com, setCom] = useState("Ashutosh");

    const [theme, setTheme] = useState('light');
    const toggleTheme = () => {
        setTheme(theme === 'light' ? 'dark' : 'light');
    };

    useEffect(() => {
        console.log("CLicked Number Chnage");
    }, [count]);

    const func = (a) => {
        if (a === 1) {
            setCount(count + 1)
        } else if (a === 2) {
            if (count > 0) {
                setCount(count - 1)
            }
        } else {
            setCount(0)
        }
    }

    const initstate = 0;

    const reducer = (state, action) => {
        switch (action) {
            case "INC":
                return state + 1;
            case "DEC":
                return state - 1;
            case "RESET":
                return initstate;

        }
    }

    const [countt, dispatch] = useReducer(reducer, initstate);

    return (
        // <ThemeContext.Provider value={{ theme, toggleTheme }}>
        <div className={`mainDiv ${theme}`}>
            <div className={"mainHead"}>
                <h1>Basics</h1>
            </div>
            <div className="mainContent">
                <div className="left">
                    <h2 id="cnt" style={count > 10 ? { color: "green", textDecoration: "underline" } : { color: "rgb(126, 41, 70);" }}>Count is : {count}</h2>
                    <button className="btns" onClick={() => func(2)}>Decrease</button>
                    <button className="btns" onClick={() => func(1)}>Increase</button>
                    <button className="btns" onClick={() => func(3)}>Reset</button>
                </div>
                <div className="centre">
                    {/* 22nd Class  */}
                    <h2>{com}</h2>
                    <button className="btns" onClick={() => { com === "Ashutosh" ? setCom("kumar") : setCom("Ashutosh") }}>change</button>
                    <p>1977, pagol</p>
                </div>
                <div className="right">
                    {/* Test_Learn */}
                    <header>
                        <h2>Switch Themes</h2>
                    </header>
                    <button className="btns" onClick={toggleTheme} style={{ textAlign: 'center' }}>
                        {theme === 'light' ? 'Switch to Dark Mode' : 'Switch to Light Mode'}
                    </button>
                </div>
                <div className="center">
                    {/* Test_Learn */}
                    <div>
                        <h1>{countt}</h1>
                        <button type='button' onClick={() => dispatch("INC")} >INC</button>
                        <button type='button' onClick={() => dispatch("DEC")} >DEC</button>
                        <button type='button' onClick={() => dispatch("RESET")} >RESET</button>
                    </div>
                </div>
            </div>
        </div>
        // </ThemeContext.Provider>
    );
};

export default First20feb;
